import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axiosInstance from '../../api/axiosInstance';

// Определите интерфейс для ваших учетных данных
interface Credentials {
  username: string;
  password: string;
}

// Определите интерфейс для состояния
interface UserState {
  user: null | object; // Замените 'object' на соответствующий тип, если нужно
  status: 'idle' | 'loading' | 'succeeded' | 'failed';
  error: null | string; // Указываем, что ошибка может быть строкой или null
}

export const register = createAsyncThunk<string, Credentials>('user/register', async (credentials, { rejectWithValue }) => {
  try {
    const response = await axiosInstance.post('/register', credentials);
    return response.data;
  } catch (error) {
    const err = error as { response: { data: string } }; // Измените на соответствующий тип ошибки
    console.error(err.response.data); // Выводим сообщение об ошибке в консоль
    return rejectWithValue(err.response.data);
  }
});

export const login = createAsyncThunk<string, Credentials>('user/login', async (credentials) => {
  const response = await axiosInstance.post('/login', credentials);
  return response.data;
});

const userSlice = createSlice({
  name: 'user',
  initialState: {
    user: null,
    status: 'idle',
    loading: false,
    error: null,
  } as UserState, // Указываем тип начального состояния
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(login.fulfilled, (state, action) => {
        state.user = action.payload;
        localStorage.setItem('token', action.payload.token);
      })
      .addCase(register.fulfilled, (state, action) => {
        state.user = action.payload;
        localStorage.setItem('token', action.payload.token);
      })
      .addCase(register.rejected, (state, action) => {
        state.error = action.payload as string; // Явно указываем, что action.payload - это строка
      });
  },
});

export default userSlice.reducer;
