import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axiosInstance from '../../api/axiosInstance';

// Обновляем тип credentials, чтобы использовать username вместо email
export const register = createAsyncThunk('user/register', async (credentials: { username: string; password: string }, { rejectWithValue }) => {
  try {
    const response = await axiosInstance.post('/register', credentials);
    return response.data;
  } catch (error) {
    console.error(error.response.data); // Выводим сообщение об ошибке в консоль
    return rejectWithValue(error.response.data);
  }
});

// Обновляем функцию login для соответствия типам
export const login = createAsyncThunk('user/login', async (credentials: { username: string; password: string }) => {
  const response = await axiosInstance.post('/login', credentials);
  return response.data;
});

const userSlice = createSlice({
  name: 'user',
  initialState: {
    user: null,
    status: 'idle',
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(login.fulfilled, (state, action) => {
        state.user = action.payload;
        localStorage.setItem('token', action.payload.token);
      })
      .addCase(register.fulfilled, (state, action) => {
        state.user = action.payload;
        localStorage.setItem('token', action.payload.token);
      })
      .addCase(register.rejected, (state, action) => {
        state.error = action.payload; // Сохраняем сообщение об ошибке в состоянии
      });
  },
});

export default userSlice.reducer;
